mapper.insert:
insert into cart(
    username,
    cartItemId,
    quantity
)
values(
    #{username},
    #{cartItemId},
    #{quantity}
)

mapper.update:
update cart set
    username = #{username},
    cartItemId = #{cartItemId},
    quantity = #{quantity}
where username = #{username}
and cartItemId = #{cartItemId}
